mapper.update:
UPDATE
aliexpress_email_template
set STATUS= #{status,jdbcType=VARCHAR}
WHERE
ID =
#{templateId}

mapper.select:
SELECT t0.ID as id, t0.SOURCE as source, t0.destination as destination, t0.buyer_login_id as buyer_login_id, t0.order_id as order_id, t0.created_at as created_at t0.order_status as order_status, t1.name as seller FROM aliexpress_email_send_history LEFT JOIN ( select it0.id, it0.name, FROM aliexpress_seller_account ) AS t1 ON t1.id = t0.seller_id
WHERE 1=1
t0.ID = #{messageHistoryId}
